set @first = 0;
set @lastt = 0;
SELECT concept_id into @first FROM concept_name WHERE name='Nursery' limit 1;
SELECT concept_id into @last FROM concept_name WHERE name='Other Education' limit 1;
update concept_name set uuid = uuid() where uuid is null and concept_id between @first and @last;
update concept set uuid = uuid() where uuid is null and concept_id between @first and @last;
